import { useState } from 'react';
import { Navigate, Route, Routes } from "react-router-dom";

import { NavBar } from './navBarFolder/navBar';
import { routeList } from '../data/routeList';
import { CardListComponent } from './cardListFolder/cardListComponent';
import { NewCardComponent } from './createCardFolder/newCardComponent';
import { CompletedCardsComponent } from './completedCardsFolder/completedCardsComponent';
import { TrashedCards } from './cardsTrashedFolder/trashedCards';
import { MoreClientData } from './moreClientDataFolder/moreClientData';

import '../styles/homeStyle.css'
import { MyProvider } from '../data/context/myProvider';


export const CurrentComponent = () => {

  const [ routesList , setRoutesList ] = useState( routeList() ); // routeList se refiere a la lista de rutas(direcciónes) que el usuario crea, estas direcciónes pueden tener más datos como telefono,nombre, correo etc

  const [ routesCompleted, setRoutesCompleted ] = useState([]); // este estado contiene las direcciónes que el usuario marcó como completadas

  const [ routesTrashed, setRoutesTrashed ] = useState([]); // este estado contiene las direcciónes que el usuario elimino, sirve como papelera


  const [ currentClientData, setCurrentClientData ] = useState({}); //este estado se refiere a una unica dirección la cual se define con una interacción especifica del usuario ( dar click a un boton para mostrar más info sobre esa dirección )

  const [ showMore, setShowMore ] = useState( false ); //este estado varia entre true y false para crear el componente que mostará la info adicional que da currentClientData 

    
  return (
    <>



      <NavBar/>

      
      <Routes>

        <Route path = "home" element = { 

          <CardListComponent   

            routesList = { routesList } 
            setRoutesList = { setRoutesList }  

            routesCompleted = { routesCompleted } 
            setRoutesCompleted = { setRoutesCompleted }

            routesTrashed = { routesTrashed }
            setRoutesTrashed = { setRoutesTrashed }

            setCurrentClientData = { setCurrentClientData }

            setShowMore = { setShowMore }/> }>

        </Route>



        <Route path = "newRoute" element = { 

          <NewCardComponent    
            setRoutesList = { setRoutesList } 
            routesList = { routesList }/> }>

        </Route>



        <Route path = "completed" element = { 

          <CompletedCardsComponent 
            routesCompleted={ routesCompleted }
            setCurrentClientData = { setCurrentClientData }
            setShowMore = { setShowMore }/> }>

        </Route>



        <Route path = "trash" element = { 

          <TrashedCards 
            routesTrashed = {routesTrashed}
            setRoutesTrashed = { setRoutesTrashed }
            routesList = { routesList }
            setRoutesList= {setRoutesList}
            setShowMore = { setShowMore }
            setCurrentClientData={setCurrentClientData } /> }>

        </Route>


        <Route path = "*" element = { <Navigate to = "home" ></Navigate> }></Route>

      </Routes>

      { showMore ? <MoreClientData  clientData = { currentClientData }
                                    setShowMore = { setShowMore }/>:null}

                                     
  </>
     
  )
}
