import { ClientCardData } from "../cardListFolder/clientCardData"


export const TrashedCards = ( { routesTrashed,
                                setRoutesTrashed,
                                routesList, 
                                setRoutesList,
                                setShowMore,
                                setCurrentClientData } ) => {

  const recover = ( clientData )=>{

    setRoutesList([...routesList, clientData])
    
    
    const filterList = routesTrashed.filter((e)=>  e.idClient != clientData.idClient );
 
    setRoutesTrashed(filterList);
    
  }


  return (
      <>
          <section className = 'RoutesListContainer'>

            { routesTrashed.map( (e)=>
            
              <div key = { e.idClient } className='clientCard'>

                <ClientCardData clientData = { e }
                                setCurrentClientData = { setCurrentClientData}
                                setShowMore = { setShowMore }/>

                <button onClick={()=> recover(e) }
                        style = { {backgroundColor:'#a0e7f2',
                        border:'none',
                        borderRadius:'20px',
                        fontWeight:'bold'}}> 

                  RECUPERAR  
                
                </button>

              </div>
            
            ) }

          </section>

      </>
  )  
}
