import { useState } from 'react';
import { Navigate, Route, Routes } from "react-router-dom";

import { NavBar } from './navBarFolder/navBar';
import { routeList } from '../data/routeList';
import { RouteListComponent } from './RouteListFolder/routeListComponent';
import { NewRouteComponent } from './createRouteFolder/newRouteComponent';
import { CompletedRoutesComponent } from './completedRoutesFolder/completedRoutesComponent';
import { TrashedRoutes } from './routesTrashedFolder/TrashedRoutes';

import '../styles/homeStyle.css';
import '../styles/temporal.css'


export const CurrentComponent = () => {

  const [ routesList , setRoutesList ] = useState( routeList() );

  const [ routesCompleted, setRoutesCompleted ] = useState([]);

  const [ routesTrashed, setRoutesTrashed ] = useState([]);

  const [ showFilters, setShowFilters] = useState(false)

  
  const sortList = ( condition )=>{
    
    // sortlist se encarga de organizar la lista de rutas, condition = true ( de menor a mayor ) condition = false ( de mayor a menor )

    const sortedList = condition ? 
    [...routesList].sort( (a,b)=> b.directionRoute.localeCompare(a.directionRoute) ) : 
    [...routesList].sort( (a,b)=> a.directionRoute.localeCompare(b.directionRoute) )

    setRoutesList( sortedList );
    setShowFilters(false)

  } 

    
  return (
    <>

    <NavBar/>


    <Routes>

      <Route path = "home" element = { 

        <RouteListComponent   routesList = { routesList } 
                              setRoutesList = { setRoutesList }  
                              routesCompleted={ routesCompleted } 
                              setRoutesCompleted = { setRoutesCompleted }
                              routesTrashed = { routesTrashed }
                              setRoutesTrashed = { setRoutesTrashed }/> }>

      </Route>



      <Route path = "newRoute" element = { 

        <NewRouteComponent    setRoutesList = { setRoutesList } 
                              routesList = { routesList }/> }>

      </Route>



      <Route path = "completed" element = { 

        <CompletedRoutesComponent routesCompleted={routesCompleted} /> }>

      </Route>

      <Route path = "trash" element = { 

        <TrashedRoutes routesTrashed = {routesTrashed} /> }>

      </Route>


      <Route path = "*" element = { <Navigate to = "home" ></Navigate> }></Route>

    </Routes>
         
    </>
     
  )
}
