import { ClientCardData } from './clientCardData';
import { ClientCardOptions } from './clientCardOptions';

import '../../styles/routesListComponentStyle.css';
import '../../styles/temporal.css'

export const RouteListComponent = ({ routesList, setRoutesList, routesCompleted, setRoutesCompleted, routesTrashed, setRoutesTrashed }) => {

//Este componente se encarga de dibujar una lista con las direcciónes que el usuario ingresa usando un map
//ClientCardData muestra los datos que se ingresaron, ClientCardOptions contiene dos botones con los que se puede interactuar con cada elemento de manera individual
    
const [ showFilters, setShowFilters] = useState(false)

  
const sortList = ( condition )=>{
  
  // sortlist se encarga de organizar la lista de rutas, condition = true ( de menor a mayor ) condition = false ( de mayor a menor )

  const sortedList = condition ? 
  [...routesList].sort( (a,b)=> b.directionRoute.localeCompare(a.directionRoute) ) : 
  [...routesList].sort( (a,b)=> a.directionRoute.localeCompare(b.directionRoute) )

  setRoutesList( sortedList );
  setShowFilters(false)

} 



  return (
    <>

      <div className='FilterComponent'>

        <button onClick = {()=> setShowFilters(!showFilters)} className='buttonFilter'> Organizar direcciónes </button>

        <div className = { showFilters? 'filterContainerOn':'filterContainerOff' }>

          <button className='filterOption' onClick={()=> sortList( true )}> Mayor a Menor </button>
          <button className='filterOption' onClick={()=> sortList( false )}> Menor a Mayor </button>

        </div>
      </div>

      <section className = 'RoutesListContainer'>

        { routesList.map( (e)=>
        
          <div key = { e.id } className='clientCard'>

            <ClientCardData clientData = { e }/>

            <ClientCardOptions  clientData = { e } 
                                setRoutesList = { setRoutesList } 
                                routesList = { routesList }
                                routesCompleted = { routesCompleted }
                                setRoutesCompleted = { setRoutesCompleted }
                                routesTrashed = { routesTrashed }
                                setRoutesTrashed = { setRoutesTrashed }/>

          </div>
        
        ) }

        </section>
    </>
   
  )
}
