export const ClientCardOptions = ( { clientData, setRoutesList, routesList,routesCompleted,setRoutesCompleted, routesTrashed,setRoutesTrashed } ) => {

  

  const deleteCard = ()=>{

    setRoutesTrashed([ ...routesTrashed, clientData ]);



  };

  const addCompletedRoute = ()=>{

    setRoutesCompleted([ ...routesCompleted, clientData ]);

    const filterList = routesList.filter((e)=>  e.id != clientData.id );
 
    setRoutesList(filterList);
    
    console.log('ruta completada')  // AGREGAR AQUÍ ESTILO FONDO VERDE

  };


  return (

    <>
        <div className='routesOptionsButtons'>

            <button className = 'CompleteButton' onClick={ ()=> addCompletedRoute() }> COMPLETADO </button>
            <button className = 'DeleteButton' onClick={ ()=> deleteCard()  }> Eliminar </button>

        </div>
    
    </>

  )
}
