import { useState } from 'react'
import '../../styles/newRouteStyle.css'

export const NewRouteComponent = ( { setRoutesList, routesList } ) => {

    const [ inputsClient, setInputsClient ] = useState({}); //este estado crea un objeto temporal y sus propiedades cambian segun los input con los que se interactue

    const [ directionType, setDirectionType ] = useState('CRR'); //estado usado en un placeholder y para crear un propiedad que hace parte del objeto que pasara a routeList

    const [showMoreOptions, setShowMoreOptions] = useState( false ); //estado encargado de mostrar u ocultar los inputs adicionales

    
    const onChangeClientData =  ( { target } )=>{

        switch(target.id){

            case 'direcType' : setInputsClient({...inputsClient, direcType : target.value});break;

            case 'num' : setInputsClient({...inputsClient, num : target.value });break;

            case 'optionalData': setInputsClient({...inputsClient, otherClientData:{ directionComplement : target.value } }) ;break;

            case 'phone' : setInputsClient({...inputsClient, phone : target.value});break;

            case 'clientname' : setInputsClient({...inputsClient, clientName : target.value});break;

            case 'otherPhone' : setInputsClient({...inputsClient, otherPhone : target.value});break;

            case 'hour' : setInputsClient({...inputsClient, hour : target.value});break;

            case 'leaveInEntry' : setInputsClient({...inputsClient, leaveInEntry : target.value});break;

            case 'freeText' : setInputsClient({...inputsClient, otherClientData:{ freeTextComplement : target.value }});break;

        }


    };

    const cleanInputs = ()=>{

        document.querySelector('#direcType').value = '';

        document.querySelector('#num').value = '';

        document.querySelector('#phone').value = '';
        
        document.querySelector('#clientname').value = '';

        document.querySelector('#optionalData').value = '';
       
    };

    const onSubmitAddClient = ( e )=>{

        e.preventDefault();

        const generateId = Math.floor(Math.random()*1000);

    
        setRoutesList([...routesList,{

            //la propiedad directionRoute concatena las propiedades direcType y num del estado inputClient
            //la propiedad num antes de concatenarse verifica si comienza con el simbolo #, si no lo tiene lo agrega

            directionRoute: `${directionType} ${ inputsClient.direcType} ${!inputsClient.num.startsWith('#')? '#'+ inputsClient.num :
                            inputsClient.num}`,
            clName: inputsClient.clientName,
            phone: inputsClient.phone,
            otherData : inputsClient.otherClientData,
            id:  generateId,

        } ])

        cleanInputs()
        setInputsClient({})

    };

  return (
    <>
        <form onSubmit = { (e)=> onSubmitAddClient(e)} className='formContainer'  id='formu'>

            <section className='directionInputs'>

                <label htmlFor = 'options' ></label>

                <select id ='options' onChange = { (e)=> setDirectionType(e.target.value) }>

                    <option value='CRR'> Carrera </option>
                    <option value='CLL'> Calle </option>
                    <option value='CQ'> Circular </option>
                    <option value='DG'> Diagonal </option>
                    <option value='TV'> Transversal </option>

                </select>

                <input type='text' placeholder = { directionType } onChange = { onChangeClientData } id='direcType' required/>

                <input type='text' placeholder='Num' onChange = { onChangeClientData } id='num' required/> 

                <input  type='text' placeholder='Int, Ap, Piso etc... (OPCIONAL)' 
                        onChange={ onChangeClientData } id='optionalData'/>

            </section>

            <section className='moreOptionsContainer'>

                <input  type='text' 
                        placeholder='Teléfono del cliente (OPCIONAL)' 
                        onChange = { onChangeClientData } 
                        id='phone' />

                <input  type='text' 
                        placeholder='Nombre del cliente (OPCIONAL)' 
                        onChange = { onChangeClientData } 
                        id='clientname'/>

                <input  type='text'  
                        placeholder='Correo electronico del cliente (OPCIONAL)' 
                        className= { 'moreOptions'}/> 

                {showMoreOptions?   <input type='text'  
                                        placeholder='Télefono secundario (OPCIONAL)' 
                                        className= { 'moreOptions'}
                                        onChange = { onChangeClientData }
                                        id = 'otherPhone'/>
                                    :null }

                {showMoreOptions?   <input type='text'  
                                        placeholder='Horario de entrega (OPCIONAL)' 
                                        className= { 'moreOptions'}                                        
                                        onChange = { onChangeClientData }
                                        id = 'hour'/>
                                    :null }

                {showMoreOptions?   <input type='text'  
                                        placeholder='Dejar en porteria?  si/no(OPCIONAL)' 
                                        className= { 'moreOptions'}                                        
                                        onChange = { onChangeClientData }
                                        id = 'leaveInEntry'/>
                                    :null }

            </section>

            {showMoreOptions?   <textarea   className='freeWrite' 
                                            placeholder='Escriba aquí otros detalles que quiera especificar (OPCIONAL)'
                                            onChange = { onChangeClientData }
                                            id = 'freeText'/>
                                :null }
                                            

                                        

        </form>
        
        <button onClick={()=> setShowMoreOptions(!showMoreOptions)} className='moreOptionsButton'> Más opciones... </button>

        <div className='buttonContainer'>
            <button className='addNewRouteButton' type='submit' form='formu'> Agregar </button>
        </div>
    </>
  )
}
